  if (SCICR2_SCTIE == 0U) {            /* Is the transmit interrupt already enabled? */
    SCICR2_SCTIE = 1U;                 /* If no than enable transmit interrupt */
  }
  ExitCritical();                      /* Exit the critical section */
  return ERR_OK;                       /* OK */
